import React from "react";

const Like = ({ post }) => {
  dispatch = use

  const handleLike = () => {
    const postData = {
      title: post.title,
      author: post.author,
      content:post.content,
      id: post.id,
      likes: post.likes + 1,
    };

    dispatch(addPostLike(postData));
  }

  return (
    <div>
      <img onClick={() => handleLike()} src="./icons/clap.png" className="clap" alt="clap" />
      <span>{post.likes}</span>
    </div>
  );
};

export default Like;
