      likes: userEvent.likes + 1,
    }

    dispatch(addPostLike(postData));
  }

  return (
    <div>
      <img onClick={() => handleLike()} src="./icons/clap.png" className="clap" alt="clap" />
      <span>{post.likes}</span>
    </div>
